{"settings":"{
    "editor.accessibilitySupport": "off",

    // üß† Theme & Icons
    "workbench.colorTheme": "Catppuccin Macchiato",
    "workbench.iconTheme": "vscode-icons",

    // ‚úçÔ∏è Editor Look & Feel
    "editor.fontFamily": "JetBrainsMono Nerd Font, FiraCode Nerd Font, monospace",
    "editor.fontLigatures": true,
    "editor.guides.bracketPairs": true,
    "editor.inlineSuggest.enabled": true,
    "editor.inlineSuggest.suppressSuggestions": true,

    // üíæ Files & Git
    "files.autoSave": "afterDelay",
    "git.openRepositoryInParentFolders": "always",
    "git.autofetch": true,
    "git.confirmSync": false,

    // üß± Terminal ‚Äî identical to Ghostty
    "terminal.integrated.fontFamily": "JetBrainsMono Nerd Font, FiraCode Nerd Font, monospace",
    "terminal.integrated.customGlyphs": true,
    "terminal.integrated.drawBoldTextInBrightColors": false,
    "terminal.integrated.minimumContrastRatio": 1,
    "terminal.integrated.inheritEnv": true,
    "terminal.integrated.gpuAcceleration": "on",
    "terminal.integrated.sendKeybindingsToShell": true,

    // üêö Default shell profile (Zsh + login mode)
    "terminal.integrated.defaultProfile.linux": "zsh",
    "terminal.integrated.profiles.linux": {
        "zsh": {
            "path": "/usr/bin/zsh",
            "args": ["-l"]
        }
    },

    // üß© Terminal Explorer integration
    "terminal.explorerKind": "both",

    // üß† Language-specific rules
    "[python]": {
        "editor.formatOnType": true
    },
    "[dart]": {
        "editor.formatOnSave": true,
        "editor.formatOnType": true,
        "editor.rulers": [80],
        "editor.selectionHighlight": false,
        "editor.tabCompletion": "onlySnippets",
        "editor.wordBasedSuggestions": "off"
    },

    // ‚öôÔ∏è Environment
    "remote.autoForwardPortsSource": "hybrid",
    "python.defaultInterpreterPath": "/bin/python",

    // Chat instruction cache (safe to keep)
    "chat.instructionsFilesLocations": {
        ".github/instructions": true,
        "/tmp/postman-collections-post-response.instructions.md": true,
        "/tmp/postman-collections-pre-request.instructions.md": true,
        "/tmp/postman-folder-post-response.instructions.md": true,
        "/tmp/postman-folder-pre-request.instructions.md": true,
        "/tmp/postman-http-request-post-response.instructions.md": true,
        "/tmp/postman-http-request-pre-request.instructions.md": true
    },
    "totalTypeScript.hideAllTips": false,
    "totalTypeScript.hideBasicTips": false
}"}
